import React, {Children, useState} from "react"
import './style.css';
import './symbols.css'

import './desktop.css'
import ParticleBackground from "./ParticleBackground"
import folder from './files/folder.png'
//import notes from './files/notes.png'
import insta from './files/instagram.png'
import spotify from './files/spotify.png'
import Draggable from "react-draggable";
import $ from 'jquery';
import { Modal } from './components/Modal';
import { SecModal } from './components/SecModal';
import { GlobalStyle } from './globalStyles';


function Desktop(props) {
  const [showSecModal, setShowSecModal] = useState(false);
  const [showModal, setShowModal] = useState(false);



  const openModal = () => {
      setShowModal(prev => !prev)
  }

  const openSecModal = () => {
      setShowSecModal(prev => !prev)
  }

  const getLocalPosition = () => {
    const position = localStorage.getItem('homeButtonPosition')
    const positionJSON = (position && JSON.parse(position)) || {}
    return {
      x: positionJSON.x || 0,
      y: positionJSON.y || 0
    }
  }
  const setLocalPosition = (data) => {
    localStorage.setItem('homeButtonPosition', JSON.stringify(data))
  }

  const {children, portalDomain} = props
  const [position, setPosition] = React.useState(getLocalPosition())
  const onStop1 = (e, data) => {
      e.stopPropagation()
      const { x: lastX, y: lastY } = getLocalPosition()
      setPosition({
        x: data.x,
        y: data.y
      })
      setLocalPosition({
        x: data.x,
        y: data.y
      })
      // mock onClick event, use lastX compare with this X, and lastY compare with this Y, if them is equal, do click, other do drag
      if (lastX === data.x && lastY === data.y) {
        setShowModal(prev => !prev)
      }
    }

    const {children2, portalDomain2} = props
    const onStop2 = (e, data) => {
      e.stopPropagation()
      const { x: lastX, y: lastY } = getLocalPosition()
      setPosition({
        x: data.x,
        y: data.y
      })
      setLocalPosition({
        x: data.x,
        y: data.y
      })
      // mock onClick event, use lastX compare with this X, and lastY compare with this Y, if them is equal, do click, other do drag
      if (lastX === data.x && lastY === data.y) {
        setShowSecModal(prev => !prev)
      }
    }

  return (
    <>
      <>
        <SecModal showSecModal={showSecModal} setShowSecModal={setShowSecModal} />
        <GlobalStyle />
      </>
      <>
        <Modal showModal={showModal} setShowModal={setShowModal} />
        <GlobalStyle />
        </>

    <desktop>
      <ParticleBackground />
        <span id="Symb">
        <Draggable parent="desktop">
          <div id="each-icon">
          <a id='golink' href="https://open.spotify.com/user/alinaqvi8014">
          <div id="pic"
            style={{
              background: `url(${spotify})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
          />
          </a>
            <p id="icon-name">Spotify</p>
          </div>
        </Draggable>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <a id='golink' href="https://instagram.com/naqwrld"> 
              <div id="pic"
              style={{
                background: `url(${insta})`,
                backgroundSize: 'contain',
                backgroundRepeat: 'no-repeat'
              }}
              lockAspectRatio={true} 
              />
            </a>
            <p id="icon-name">Instagram</p>
          </div>
          </Draggable>


          <Draggable parent={"desktop"}
            position={position}
            onStop={onStop1}>
            <div id="each-icon"

              onDoubleClick={openModal}

            >
            <div >
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            </div>
            <p id="icon-name">Vids</p></div>
          </Draggable>
        {children}

        <Draggable parent={"desktop"}
        position={position}
        onStop={onStop2}>
          <div id="each-icon">
            <div onDoubleClick={openSecModal}>
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            </div>
            <p id="icon-name">Art</p></div>
          </Draggable>
          {children2}


          {/*        
        <Draggable parent={"desktop"}><div id="each-icon"> 
            <div class="notes" id="pic" 
              style={{
              background: `url(${notes})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat',
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p>
            </div>
          </Draggable>
          */}
        </span>
      </desktop>
    </>

  );

}

export default Desktop;
