import React from 'react';
import styled from "styled-components";
import { MdClose } from 'react-icons/md';

const Background = styled.div`
    z-index: 1000000;
    top: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    position: fixed;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const ModalWrapper = styled.div`
width: 100vh;
height: 90vh;
box-shadow: 0 5px 16px rgba(0, 0, 0.2);
background: #202124;
color: #000;
display: grid;
grid-template-columns: 1fr 1fr;
position: relative;
z-index: 100000000;
border-radius: 10px;
`;


const CloseModalButton = styled(MdClose)`
    cursor: pointer;
    position: absolute;
    top: 20px;
    right: 20px;
    width: 32px;
    height: 32px;
    padding: 0;
    z-index: 10;
`;

const Thing = styled.div`
${CloseModalButton}:hover & {
    background: #000;
    color: #000;
  }

`;

const ModalContent = styled.div`
  display:flex;
  justify-direction: column;
  justify-content: center;
  align-items: center;
  line-height: 1.8;
  color: #141414;
`;


export const SecModal = ({showModal, setShowModal}) => {
    
    
    return (
    <>
        {showModal ? (
        <Background>
            <ModalWrapper showModal={showModal}>
                <CloseModalButton aria-label='Close modal' onClick={() => setShowModal
                    (prev => !prev)}><Thing /></CloseModalButton>
                
                <ModalContent>
                     <title style="color:white;">Videos</title>       
                </ModalContent>
            </ModalWrapper>
        </Background>
        ) : null}
    </>        
    )
};
