import React, {useRef} from 'react';
import styled from "styled-components";
import { MdClose } from 'react-icons/md';

const Background = styled.div`
    z-index: 1000000;
    top: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    position: fixed;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const ModalWrapper = styled.div`
width: 100vh;
height: 90vh;
box-shadow: 0 5px 16px rgba(0, 0, 0.2);
background: #202124;
color: #000;
display: grid;
grid-template-columns: 1fr 1fr;
position: relative;
z-index: 100000000;
border-radius: 7px;
`;


const CloseModalButton = styled(MdClose)`
    cursor: pointer;
    position: absolute;
    top: 0vh;
    color: grey;
    overflow: hidden;
    right: 0vh;
    width: 28px;
    height: 26px;
    padding-top: 0.6vh;
    padding-right: 1vh;
    padding-bottom: 0.3vh;
    padding-left: 1vh;
    border-radius: 0 7px 0 0;
    z-index: 10;
    :hover{
     background-color: #be0000;
     color: white;   
    }
`;


const ModalContent = styled.div`
  display:flex;
  justify-direction: column;
  justify-content: center;
  align-items: center;
  line-height: 1.8;
  color: #141414;

  .title{
      color: white;
  }
`;

const Head = styled.div`
  z-index: 10000;
  border-radius: 7px 7px 0 0;
  border-rad
  top: 0%;
  min-width: 100%;
  height: 4vh;
  background-color: #303134;
`;


export const SecModal = ({showModal, setShowModal}) => {
    
    
    return (
    <>
        {showModal ? (
        <Background>
            <ModalWrapper showModal={showModal}>
                <Head>
                <CloseModalButton aria-label='Close modal' onClick={() => setShowModal
                    (prev => !prev)}></CloseModalButton>
                </Head>
                <ModalContent>
                     <title>Videos</title>       
                </ModalContent>
            </ModalWrapper>
        </Background>
        ) : null}
    </>        
    )
};
