import React from "react"
import './footer.css'
/*
import PlayCircleOutlineIcon from "@mui/icons-material/PlayCircle"
import { Grid, Slider } from "@material-ui/core";
import VolumeDownIcon from '@mui/icons-material/VolumeDown';
import PauseCircleOutlineIcon from '@mui/icons-material/PauseCircle';
*/
import kitch from './files/kitch.mp3' 
import cudi from './files/cudi.jpg'
import AudioPlayer from 'material-ui-audio-player';
import { createMuiTheme, ThemeProvider } from '@material-ui/core';
import { makeStyles } from 'styled-components'




export default function Footer(){

    const muiTheme = createMuiTheme({});
    const useStyles = makeStyles`
          root: {
            [theme.breakpoints.down('sm')]: {
              width: '100%',
            },
          },

          playIcon: {
            color: '#f50057',
            '&:hover': {
              color: '#ff4081',
            },
          },

          pauseIcon: {
            color: '#0099ff',
          },
          volumeIcon: {
            color: 'rgba(0, 0, 0, 0.54)',
          },
          volumeSlider: {
            color: 'black',
          },
          progressTime: {
            color: 'rgba(0, 0, 0, 0.54)',
          },
          mainSlider: {
            color: '#3f51b5',
            '& .MuiSlider-rail': {
              color: '#7986cb',
            },
            '& .MuiSlider-track': {
              color: '#3f51b5',
            },
            '& .MuiSlider-thumb': {
              color: '#303f9f',
            },
          }
`

    return (
         <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src={cudi} alt="" />
                <div className="footer-songInfo">
                    <h4>Kitchen</h4>
                    <p>Kid Cudi</p>
                </div>
            </div>      
            <ThemeProvider theme={muiTheme}>
            <AudioPlayer
            width="60%"
            variation="default"
            order="standart"
            preload="auto"
            useStyles={useStyles}
            src={kitch}

                />
            </ThemeProvider>    


        </div>
    );
};


