import React from "react"
import './footer.css'
import PlayCircleOutlineIcon from "@mui/icons-material/PlayCircle"
import { Grid, Slider } from "@material-ui/core";
import VolumeDownIcon from '@mui/icons-material/VolumeDown';
import PauseCircleOutlineIcon from '@mui/icons-material/PauseCircle';
import kitch from './files/kitch.mp3' 
import cudi from './files/cudi.jpg'
import AudioPlayer from 'material-ui-audio-player';


export default function Footer(){
    const [playing, setPlaying] = React.useState(false);

    const audioRef = React.useRef(null);
    const togglePlaying = () => setPlaying(prev => !prev);
    const icons = {
        PlayIcon: PlayCircleOutlineIcon,
        PauseIcon: PauseCircleOutlineIcon
    }

    React.useEffect(() => {
        if(audioRef && audioRef.current) {
            if(playing) audioRef.current.play();
            else audioRef.current.pause();
        }
    }, [playing]) 

    return (
         <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src={cudi} alt="" />
                <div className="footer-songInfo">
                    <h4>Kitchen</h4>
                    <p>Kid Cudi</p>
                </div>
            </div>      
            <AudioPlayer
            elevation={1}
            width="100%"
            variation="default"
            spacing={3}

            order="standart"
            preload="auto"
            loop={true}
            src={kitch}
            icons = {icons}
            />


        </div>
    );
};


