import React from "react"
import './footer.css'
import PlayCircleOutlineIcon from "@mui/icons-material/PlayCircle"
import { Grid, Slider } from "@material-ui/core";
import VolumeDownIcon from '@mui/icons-material/VolumeDown';
import PauseCircleOutlineIcon from '@mui/icons-material/PauseCircle';
import kitch from './files/kitch.mp3' 
import cudi from './files/cudi.jpg'


export default function Footer(props){
    const [playing, setPlaying] = React.useState(false);


    const audioRef = React.useRef(null);
    const togglePlaying = () => setPlaying(prev => !prev) 

    const audioIsPlaying = (e) => {
        this.setState({ currentTime: Math.floor(e.target.currentTime) });
    };


    React.useEffect(() => {
        if(audioRef && audioRef.current) {
            if(playing) audioRef.current.play();
            else audioRef.current.pause();
        }
    }, [playing])

    const handleChange = (event, newValue) => {
        return { currentTime: newValue };
    };


    return (
         <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src={cudi} alt="" />
                <div className="footer-songInfo">
                    <h4>Kitchen</h4>
                    <p>Kid Cudi</p>
                </div>
            </div>
        <div className="footer-center" >
                    <audio src={kitch} onLoadedMetadata={(event) => this.audioWasLoaded(event)}
                        onEnded={(event) => this.audioEnded(event)}
                        onTimeUpdate={(event) => audioIsPlaying(event)}
                        ref={audioRef} />
                        {playing ? ( 
                        <PauseCircleOutlineIcon fontSize="large" className="footer-icon" transform="scale(4)" onClick={togglePlaying}/>
                        )  :  (
                        <PlayCircleOutlineIcon fontSize="large" className="footer-icon" transform="scale(4)" onClick={togglePlaying}/>
                        )}
                    </div>

                    <div className="footer-right">
                        <Grid container spacing={2}>
                            <Grid item>
                        <VolumeDownIcon
                            value={(this.state.currentTime * 100) / this.state.duration}
                            onChange={handleChange}
                        />
                            </Grid>
                            <Grid item xs>
                                <Slider/>
                            </Grid>
                        </Grid>    
                    </div>

        </div>
    );
};
