import React, {useState} from "react"
import './style.css';
import './symbols.css'


import './desktop.css'
import ParticleBackground from "./ParticleBackground"
import folder from './files/folder.png'
import notes from './files/notes.png'
import insta from './files/instagram.png'
import spotify from './files/spotify.png'
import Draggable from "react-draggable";
import $ from 'jquery';
import './components/modal.css'



function Desktop() {
  //for icons hyperlink, double click not click
  $(function($) {
    $('#golink').click(function() {
        return false;
    }).dblclick(function() {
        window.location = this.href;
        return false;
    });
  });

  /*first is value for state, second is function to update state
   const [showModal, setShowModal] = useState(false)

  const openModal = () => {
    setShowModal(prev => !prev)
  } */

    // Get the button that opens the modal
  var btn = document.querySelectorAll("button.modal-button");

  // All page modals
  var modals = document.querySelectorAll('.modal');

  // Get the <span> element that closes the modal
  var spans = document.getElementsByClassName("close");

  // When the user clicks the button, open the modal
  for (var i = 0; i < btn.length; i++) {
  btn[i].onclick = function(e) {
      e.preventDefault();
      modal = document.querySelector(e.target.getAttribute("href"));
      modal.style.display = "block";
  }
  }

  // When the user clicks on <span> (x), close the modal
  for (var i = 0; i < spans.length; i++) {
  spans[i].onclick = function() {
      for (var index in modals) {
        if (typeof modals[index].style !== 'undefined') modals[index].style.display = "none";    
      }
  }
  }

  // When the user clicks anywhere outside of the modal, close it
  window.onclick = function(event) {
      if (event.target.classList.contains('modal')) {
      for (var index in modals) {
        if (typeof modals[index].style !== 'undefined') modals[index].style.display = "none";    
      }
      }
  }


  return (
    <desktop>
      <ParticleBackground />
      <span id="Symb">

        <Draggable parent={"desktop"}>
          <div id="each-icon">
          <a id='golink' href="https://open.spotify.com/user/naqwrld">
          <div id="pic"
            style={{
              background: `url(${spotify})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
          />
          </a>
            <p id="icon-name">Spotify</p>
          </div>
        </Draggable>

    <div id="myModal1" class="modal">

      <div class="modal-content">
        <div class="modal-header">
          <span class="close">×</span>
          <h2>Modal Header</h2>
        </div>
        <div class="modal-body">
          <p>Some text in the Modal Body</p>
          <p>Some other text...</p>
        </div>
        <div class="modal-footer">
          <h3>Modal Footer</h3>
        </div>
      </div>

    </div>

    <h2>2nd Modal</h2>

    <button class="modal-button" href="#myModal2">Open Modal</button>

    <div id="myModal2" class="modal">

      <div class="modal-content">
        <div class="modal-header">
          <span class="close">×</span>
          <h2>Modal Header</h2>
        </div>
        <div class="modal-body">
          <p>Some text in the Modal Body</p>
          <p>Some other text...</p>
        </div>
        <div class="modal-footer">
          <h3>Modal Footer</h3>
        </div>
      </div>
    </div>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <a id='golink' href="https://instagram.com/naqwrld"> 
              <div id="pic"
              style={{
                background: `url(${insta})`,
                backgroundSize: 'contain',
                backgroundRepeat: 'no-repeat'
              }}
              lockAspectRatio={true} 
              />
            </a>
            <p id="icon-name">Instagram</p>
          </div>
        </Draggable>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <div class="modal-button" href="#myModal1">
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            </div>
            <p id="icon-name">Vids</p></div>
        </Draggable>

        <Draggable parent={"desktop"}>
        <div id="each-icon"> 
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p></div>
        </Draggable>

        <Draggable parent={"desktop"}>
        <div id="each-icon"> 
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p></div>
        </Draggable>

        <Draggable parent={"desktop"}><div id="each-icon"> 
            <div id="pic" 
            style={{
              background: `url(${notes})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat',
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p>
            </div>

        </Draggable>
        </span>
    </desktop>

  );

}

export default Desktop;
