import React, { useEffect, useRef, useState } from "react"
import './footer.css'
import PlayCircleOutlineIcon from "@mui/icons-material/PlayCircle"
import { Grid, Slider } from "@material-ui/core";
import VolumeDownIcon from '@mui/icons-material/VolumeDown';
import PauseCircleOutlineIcon from '@mui/icons-material/PauseCircle';
import kitch from './files/kitch.mp3' 
import cudi from './files/cudi.jpg'
import { fromEvent, merge } from "rxjs";
import { switchMap, takeUntil, map } from "rxjs/operators";


export default function Footer(){
    const [playing, setPlaying] = React.useState(false);

    const volumeRef = useRef(null);
  const [volume, setVolume] = useState(0);
    const [thumbPos, setThumPos] = useState(0);



    const audioRef = React.useRef(null);
    const togglePlaying = () => setPlaying(prev => !prev) 

    React.useEffect(() => {
        if(audioRef && audioRef.current) {
            if(playing) audioRef.current.play();
            else audioRef.current.pause();
        }
        const { current: ref } = volumeRef;
        const end$ = merge(fromEvent(ref, "mouseup"), fromEvent(window, "mouseup"));
        const mousemove$ = fromEvent(ref, "mousemove").pipe(takeUntil(end$));
        const mouseDown$ = fromEvent(ref, "mousedown").pipe(
        switchMap(() => mousemove$),
        map(e => e.offsetX / ref.clientWidth),
        map(ratio => Math.min(Math.max(ratio, 0), 1))
    );
        const draggingSub = mouseDown$.subscribe(ratio => {
            setVolume(ratio);
            setThumPos(ratio * ref.clientWidth);
        });
    return () => {
      draggingSub.unsubscribe();
    };
    }, [playing])

    return (
         <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src={cudi} alt="" />
                <div className="footer-songInfo">
                    <h4>Kitchen</h4>
                    <p>Kid Cudi</p>
                </div>
            </div>
        <div className="footer-center" >
                    <audio src={kitch} ref={audioRef} />
                        {playing ? ( 
                        <PauseCircleOutlineIcon fontSize="large" className="footer-icon" transform="scale(4)" onClick={togglePlaying}/>
                        )  :  (
                        <PlayCircleOutlineIcon fontSize="large" className="footer-icon" transform="scale(4)" onClick={togglePlaying}/>
                        )}
                    </div>

                    <div className="footer-right">
                        <Grid container spacing={2}>
                            <Grid item>
                        <VolumeDownIcon
                            value={volume}
                            thumbPosition={thumbPos} 
                            ref={volumeRef} 
                            />
                            </Grid>
                            <Grid item xs>
                                <Slider/>
                            </Grid>
                        </Grid>    
                    </div>

        </div>
    );
};
