import React, {useRef} from 'react';
import styled from "styled-components";
import { MdClose } from 'react-icons/md';

const Background = styled.div`
    z-index: 1000000;
    top: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    position: fixed;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const ModalWrapper = styled.div`
width: 100vh;
height: 90vh;
box-shadow: 0 5px 16px rgba(0, 0, 0.2);
background: #202124;
color: #000;
display: grid;
position: relative;
z-index: 100000000;
border-radius: 7px;
min-width: auto; 
`;


const CloseModalButton = styled(MdClose)`
    cursor: pointer;
    position: absolute;
    top: 0vh;
    color: grey;
    overflow: hidden;
    right: 0%;
    width: 28px;
    height: 26px;
    padding-top: 0.9vh;
    padding-right: 1vh;
    padding-bottom: 0.3vh;
    padding-left: 1vh;
    border-radius: 0 7px 0 0;
    z-index: 10;
    :hover{
     background-color: #be0000;
     color: white;   
    }
`;


const ModalContent = styled.div`
    top: 5vh;
  display: grid;
  justify-content: center;
`;

const Head = styled.div`
  width: 100%;
  border-radius: 7px 7px 0 0;
  top: 0%;
  position: relative;
  height: 4vh;
  background-color: #303134;
`;


export const Modal = ({showModal, setShowModal}) => {
    
    
    return (
    <>
        {showModal ? (
        <Background>
            <ModalWrapper showSecModal={showModal}>
                <Head>
                <CloseModalButton aria-label='Close modal' onClick={() => setShowModal
                    (prev => !prev)}></CloseModalButton>
                </Head>
                <ModalContent>
                            <iframe width="560" height="315" src="https://www.youtube.com/embed/THPONVgoLoQ"
                                title="YouTube video player" frameborder="0"
                                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                allowfullscreen="allowfullscreen"></iframe>
                            
                            <iframe width="560" height="315" src="https://www.youtube.com/embed/YbPL9EICD7Y"
                                title="YouTube video player" frameborder="0"
                                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                allowfullscreen="allowfullscreen"></iframe>
                </ModalContent>
            </ModalWrapper>
        </Background>
        ) : null}
    </>        
    )
};
