import React from "react"
import { makeStyles } from '@mui/styles';
import './footer.css'
import kitch from './files/kitch.mp3' 
import cudi from './files/cudi.jpg'
import AudioPlayer from 'material-ui-audio-player';
import { createMuiTheme, ThemeProvider } from '@material-ui/core';
const muiTheme = createMuiTheme({});

const useStyles = makeStyles({
    root: {
        backgroundColor: 'black !important',
        width: '80% !important',
        ['@media (max-width:975px)']: { // eslint-disable-line no-useless-computed-key
            width: '75% !important'
        },
        ['@media (max-width:810px)']: { // eslint-disable-line no-useless-computed-key
            width: '70% !important'
        },
        ['@media (max-width:664px)']: { // eslint-disable-line no-useless-computed-key
            width: '65% !important'
        },
        ['@media (max-width:587px)']: { // eslint-disable-line no-useless-computed-key
            width: '60% !important'
        },
        ['@media (max-width:512px)']: { // eslint-disable-line no-useless-computed-key
            width: '55% !important'
        },
    },
    playIcon: {
        color: '#ffffff !important',
        '&:hover': {
            color: '#ffffff !important'
         },
    },
    replayIcon: {
        color: '#ffffff !important'
    },
    pauseIcon: {
        color: '#ffffff !important'
    },
    volumeIcon: {
        color: '#ffffff !important'
    },
    volumeSlider: {
        color: '#ffffff !important'
    },
    progressTime: {
        color: '#ffffff !important'
    },
    mainSlider: {
        color: '#ffffff !important',
        '& .MuiSlider-rail': {
            color: '#ffffff !important'
        },
        '& .MuiSlider-track': {
            color: '#ffffff !important'
        },
        '& .MuiSlider-thumb': {
            color: '#ffffff !important'
        },
    },
});




export default function Footer(){


    return (
         <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src={cudi} alt="" />
                <div className="footer-songInfo">
                    <h4>Kitchen</h4>
                    <p>Kid Cudi</p>
                </div>
            </div>      
            <ThemeProvider theme={muiTheme}>
                <AudioPlayer
                    class = "audioplaya"
                    variation="default"
                    order="standart"
                    volumeSlider= "false"
                    preload="auto"
                    useStyles={useStyles}
                    src={kitch}
                />
            </ThemeProvider>    


        </div>
    );
};
