import React, {Component} from "react"
import './footer.css'
import PlayCircleOutlineIcon from "@mui/icons-material/PlayCircle"
import { Grid, Slider } from "@material-ui/core";
import VolumeDownIcon from '@mui/icons-material/VolumeDown';
import PauseCircleOutlineIcon from '@mui/icons-material/PauseCircleOutline';
import Travy from "./travy.mp3" 
import { IconButton } from "@mui/material";



class Footer extends Component {
    state = {
        audio: new Audio(Travy),
    }

    playPause = () => {
        let isPlaying = this.state.isPlaying;
        if (isPlaying) {
          this.state.audio.pause();
        } else {
          this.state.audio.play();
        }

        this.setState({isPlaying: !isPlaying});
      };

      render(){
  return (
    <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src="https://upload.wifimedia.org/wikipedi" alt="" />
                <div className="footer-songInfo">
                    <h4>Dead End</h4>
                    <p>Carti</p>
                </div>
            </div>
            <div className="footer-center">
                {isPlaying ? (
                <PauseCircleOutlineIcon fontSize="large" className="footer-icon" onClick={this.playPause} />
                ): (
                <PlayCircleOutlineIcon fontSize="large" className="footer-icon" onClick={this.playPause} />
                )}
            </div>
            <div className="footer-right">
                <Grid container spacing={2}>
                    <Grid item>
                        <VolumeDownIcon />
                    </Grid>
                    <Grid item xs>
                        <Slider/>
                    </Grid>
                </Grid>    
            </div>
        </div>
  );
}
}

export default Footer;
