import React, { useState, useCallback, useRef, useEffect, Component } from "react"
import Draggable from 'react-draggable';
import './symbols.css'
import './style.css'
import './Desktop.js'
import './desktop.css'
import folder from './folder.jpg'

const Symbol = ({ position = { x: 0, y: 0 } }) => {
  const [state, setState] = useState({
    isDragging: false,
    dX: position.x,
    dY: position.y
  });

  const onMouseMove = useCallback(
    e => {
      if (state.isDragging) {
        setState(prevState => ({
          ...prevState,
          dX: prevState.dX + e.movementX,
          dY: prevState.dY + e.movementY
        }));
      }
    },
    [state.isDragging]
  );

  const onMouseDown = useCallback(() => {
    setState(prevState => ({
      ...prevState,
      isDragging: true
    }));
  }, []);

  const onMouseUp = useCallback(() => {
    if (state.isDragging) {
      setState(prevState => ({
        ...prevState,
        isDragging: false
      }));
    }
  }, [state.isDragging]);

  useEffect(() => {
    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);

    return () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };
  }, [onMouseMove, onMouseUp]);


  return (
    <Draggable onMouseDown={onMouseDown}>
      <div id="each-icon">
        <img id="icons-pic" src={folder} alt="imp"></img>
        <p id="icon-name">{this.props.title}</p>
      </div>
    </Draggable>
  );
};

export default Symbol;
