import React from "react"
import './footer.css'
import PlayCircleOutlineIcon from "@mui/icons-material/PlayCircle"
import { Grid, Slider } from "@material-ui/core";
import VolumeDownIcon from '@mui/icons-material/VolumeDown';
import PauseCircleOutlineIcon from '@mui/icons-material/PauseCircleOutline';
import Travy from "./travy.mp3" 
import { IconButton } from "@mui/material";


export default function Footer() {
    const [playing, setPlaying] = React.useState(false);

    const audioRef = React.useRef(null);
    const togglePlaying = () => setPlaying(prev => !prev) 

    React.useEffect(() => {
        if(audioRef && audioRef.current) {
            if(playing) audioRef.current.play();
            else audioRef.current.pause();
        }
    }, [playing])

    return (
         <div className="footer">
            <div className="footer-left">
                <img className="footer-albumLogo" src="https://upload.wifimedia.org/wikipedi" alt="" />
                <div className="footer-songInfo">
                    <h4>Dead End</h4>
                    <p>Carti</p>
                </div>
            </div>
            <div className="footer-center" onClick={togglePlaying}>
            <audio src={Travy} ref={audioRef} />
                {playing ? ( 

                <PlayCircleOutlineIcon fontSize="large" className="footer-icon" src={Travy} ref ={audioRef} />
                  )  :  (
                <PauseCircleOutlineIcon fontSize="large" className="footer-icon" src={Travy} ref ={audioRef}/>
                  )}
            </div>

            <div className="footer-right">
                <Grid container spacing={2}>
                    <Grid item>
                        <VolumeDownIcon />
                    </Grid>
                    <Grid item xs>
                        <Slider/>
                    </Grid>
                </Grid>    
            </div>
        </div>
    );
};




