import React, {useState} from "react"
import './style.css';
import './symbols.css'


import './desktop.css'
import ParticleBackground from "./ParticleBackground"
import folder from './files/folder.png'
import notes from './files/notes.png'
import insta from './files/instagram.png'
import spotify from './files/spotify.png'
import Draggable from "react-draggable";
import $ from 'jquery';
import { Modal } from './components/Modal';
import { GlobalStyle } from './globalStyles'


function Desktop() {
  //for icons hyperlink, double click not click
  $(function($) {
    $('#golink').click(function() {
        return false;
    }).dblclick(function() {
        window.location = this.href;
        return false;
    });
  });

  //first is value for state, second is function to update state
  const [showModal, setShowModal] = useState(false)

  const openModal = () => {
    setShowModal(prev => !prev)
  }

  return (
    <desktop>
      <ParticleBackground />
      <span id="Symb">

        <Draggable parent={"desktop"}>
          <div id="each-icon">
          <a id='golink' href="https://open.spotify.com/user/naqwrld">
          <div id="pic"
            style={{
              background: `url(${spotify})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
          />
          </a>
            <p id="icon-name">Spotify</p>
          </div>
        </Draggable>


        <Modal showModal={showModal} setShowModal={setShowModal} />


        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <a id='golink' href="https://instagram.com/naqwrld"> 
              <div id="pic"
              style={{
                background: `url(${insta})`,
                backgroundSize: 'contain',
                backgroundRepeat: 'no-repeat'
              }}
              lockAspectRatio={true} 
              />
            </a>
            <p id="icon-name">Instagram</p>
          </div>
        </Draggable>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <div onDoubleClick={openModal}>
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            </div>
            <p id="icon-name">Vids</p></div>
        </Draggable>

        <Draggable parent={"desktop"}>
        <div id="each-icon"> 
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p></div>
        </Draggable>

        <Draggable parent={"desktop"}>
        <div id="each-icon"> 
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p></div>
        </Draggable>

        <Draggable parent={"desktop"}><div id="each-icon"> 
            <div id="pic" 
            style={{
              background: `url(${notes})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat',
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p>
            </div>

        </Draggable>
        </span>
    </desktop>

  );

}

export default Desktop;
