import React, {useState} from "react"
import './style.css';
import './symbols.css'

import './desktop.css'
import ParticleBackground from "./ParticleBackground"
import folder from './files/folder.png'
import notes from './files/notes.png'
import insta from './files/instagram.png'
import spotify from './files/spotify.png'
import Draggable from "react-draggable";
import $ from 'jquery';
import { Modal } from './components/Modal';
import { SecModal } from './components/SecModal';
import { GlobalStyle } from './globalStyles';


function Desktop() {
    const [isCommandHandled, setIsCommandHandled] = React.useState(null);
    const [buttonPressTimer, setButtonPressTimer] = React.useState(null);

    const handleButtonPress = function(e) {
      if (buttonPressTimer) clearTimeout(buttonPressTimer);

      setButtonPressTimer(setTimeout(handleLongPress, 1500, e));

      setIsCommandHandled(prevState => {
        console.log("ON_MOUSE_DOWN " + isCommandHandled); // here value null

        return false;
      });
    };

    const handleLongPress = e => {
      if (!isCommandHandled) {
        setIsCommandHandled(prevState => {
          console.log("TIMER_IS_EXECUTED", isCommandHandled); //Here value false or null
          return true;
        });

        // some other logic for long press event
      } else {
        console.log("LONG PRESS TIMER_IS_EXECUTED", isCommandHandled);
        setIsCommandHandled(prevState => {
          return true;
        });
      }
    };

    const handleButtonRelease = function(e) {
      if (!isCommandHandled) {
        console.log("SINGLE PRESS", isCommandHandled);
        //isCommandHandled isn't updated here, as a result logic is executed always
        // got regular click, not long press
        // specific logic
        setIsCommandHandled(prevState => {
          return true;
        });
      }

      clearTimeout(buttonPressTimer);
    };


  const [showSecModal, setShowSecModal] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const openModal = () => {
    setShowModal(prev => !prev)
  }

  const openSecModal = () => {
    setShowSecModal(prev => !prev)
  }

  return (
    <>
      <>
        <SecModal showSecModal={showSecModal} setShowSecModal={setShowSecModal} />
        <GlobalStyle />
      </>
      <>
        <Modal showModal={showModal} setShowModal={setShowModal} />
        <GlobalStyle />
        </>

    <desktop>
      <ParticleBackground />
        <span id="Symb">
        <Draggable parent={"desktop"}>
          <div id="each-icon">
          <a id='golink' href="https://open.spotify.com/user/naqwrld">
          <div class="hold_trigger"id="pic"
            style={{
              background: `url(${spotify})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
          />
          </a>
            <p id="icon-name">Spotify</p>
          </div>
        </Draggable>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <a id='golink' href="https://instagram.com/naqwrld"> 
              <div id="pic" 
              style={{
                background: `url(${insta})`,
                backgroundSize: 'contain',
                backgroundRepeat: 'no-repeat'
              }}
              lockAspectRatio={true} 
              />
            </a>
            <p id="icon-name">Instagram</p>
          </div>
        </Draggable>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
              <div 
                 onMouseDown={handleButtonPress}
                 onMouseUp={handleButtonRelease}>
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            </div>
            <p id="icon-name">Vids</p></div>
        </Draggable>

        <Draggable parent={"desktop"}>
          <div id="each-icon">
            <div onDoubleClick={openSecModal}>
            <div id="pic"
            style={{
              background: `url(${folder})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat'
            }}
            lockAspectRatio={true} 
            >
            </div>
            </div>
            <p id="icon-name">Art</p></div>
        </Draggable>


          {/*        
        <Draggable parent={"desktop"}><div id="each-icon"> 
            <div class="notes" id="pic" 
              style={{
              background: `url(${notes})`,
              backgroundSize: 'contain',
              backgroundRepeat: 'no-repeat',
            }}
            lockAspectRatio={true} 
            >
            </div>
            <p id="icon-name">???</p>
            </div>
          </Draggable>
          */}
        </span>
      </desktop>
    </>

  );

}

export default Desktop;
